import "./_notification.scss"

import React, { Component } from 'react';
import { ReactComponent as AskNed } from './../../../../assets/images/profile_images/AskNed.jpeg'
import { ReactComponent as BottleUp } from './../../../../assets/images/profile_images/BottleUp.jpeg'

import { useState, useEffect, useRef } from 'react';
import { CSSTransition } from 'react-transition-group';

const data = [
    {
        image: AskNed,
        message: 'AskNed has accepted your request',
        detailPage: '/',
    },
    {
        image: BottleUp,
        message: 'You have matched with BottleUp',
        detailPage: '/',
    },
]

function Notifications(props) {
  const [open, setOpen] = useState(false);

  return (
    <li className="notifications">
      <a href="#/0" className="icon-button" onClick={() => setOpen(!open)}>
        {props.icon}
      </a>

      {open && DropdownMenu()}
    </li>
  );
}

function DropdownMenu() {
  const [activeMenu, setActiveMenu] = useState('main');
  const [menuHeight, setMenuHeight] = useState(null);
  const dropdownRef = useRef(null);

  useEffect(() => {
    setMenuHeight(dropdownRef.current?.firstChild.offsetHeight)
  }, [])

  function calcHeight(el) {
    const height = el.offsetHeight;
    setMenuHeight(height);
  }

  function DropdownItem(props) {
    return (
      <a href="#" className="menu-item" onClick={() => props.goToMenu && setActiveMenu(props.goToMenu)}>
        <span className="icon-button">{props.leftIcon}</span>
        {props.children}
        <span className="icon-right">{props.rightIcon}</span>
      </a>
    );
  }

  return (
    <div className="dropdown" style={{ height: menuHeight }} ref={dropdownRef}>

      <CSSTransition
        in={activeMenu === 'main'}
        timeout={500}
        classNames="menu-primary"
        unmountOnExit
        onEnter={calcHeight}>
        <div className="menu">
          <DropdownItem>Notification 1</DropdownItem>
          <DropdownItem>Notification 2</DropdownItem>

        </div>
      </CSSTransition>
    </div>
  );
}

export default Notifications;
