import React, { Component } from 'react';
import AskNed from './../../../../assets/images/profile_images/AskNed.jpeg'
import BottleUp from './../../../../assets/images/profile_images/BottleUp.jpeg'

const data = [
    {
        image: AskNed,
        message: 'AskNed has accepted your request',
        detailPage: '/',
    },
    {
        image: BottleUp,
        message: 'You have matched with BottleUp',
        detailPage: '/',
    },
]

class Notifications extends Component {
  constructor() {
    super();

    this.state = {
      showNotifications: false,
    }

    this.showMenu = this.showMenu.bind(this);
    this.closeMenu = this.closeMenu.bind(this);
  }

  showMenu(event) {
    event.preventDefault();

    this.setState({showMenu: true }, () => {
        document.addEventListener('click', this.closeMenu);
    });
  }

  closeMenu(event) {
    if (!this.dropdownMenu.contains(event.target)) {
      this.setState({ showMenu: false}, () => {
        document.removeEventListener('click', this.closeMenu);
      });
    }
  }

  render() {
    return (
      <div>
        <button onClick={this.showMenu}>
          Notifications
        </button>

        {
          this.state.showMenu
          ? (
            <div
              className="notifications"
              ref={(element) => {
                this.dropdownMenu = element;
              }}
            >
              <button> Menu Item 1 </button>
              <button> Menu Item 2 </button>
              <button> Menu Item 3 </button>
            </div>
          )
          : (
            null
          )
        }
      </div>

    );
  }
}

export default Notifications;

