import React, { useState } from 'react';
import Image from '../../elements/Image';

import {
  Button,
  Card,
  CardHeader,
  CardBody,
  FormGroup,
  Form,
  Input,
  Container,
  Row,
  Col
} from 'reactstrap';
// core components

import InfoDataService from './services/InfoService';

const CompanyProfile = () => {
  const initialInfoState = {
    id: null,
    about_us: '',
    fast_facts: [],
    team_members: [],
  };
  const [info, setInfo] = useState(initialInfoState);

  const [edit, setEdit] = useState(true);

  const handleSave = (e) => {
    e.preventDefault();
    saveInfo();
    //set edit to false when save is clicked
    setEdit(false);
  };

  const handleEdit = (e) => {
    e.preventDefault();
    //set edit to true when edit is clicked
    setEdit(true);
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setInfo({ ...info, [name]: value });
  };

  const saveInfo = () => {
    var data = {
      about_us: info.about_us,
      fast_facts: info.fast_facts,
      team_members: info.team_members,
    };

    if (info.id === null) {
      InfoDataService.create(data)
        .then((response) => {
          console.log('create', response.data);
          setInfo({
            id: response.data.id,
            about_us: response.data.about_us,
            fast_facts: response.data.fast_facts,
            team_members: response.data.team_members
          });
        })
        .catch((e) => {
          console.error(e);
        });
    } else {
      InfoDataService.update(info.id, data)
        .then((response) => {
          console.log(response);
          setInfo({
            id: response.data.id,
            about_us: response.data.about_us,
            fast_facts: response.data.fast_facts,
            team_members: response.data.team_members
          });
          console.log(response.data);
        })
        .catch((e) => {
          console.error(e);
        });
    }
  };

  return (
    <>
      {/* Page content */}

      <Container className="mt--7" fluid>
        <Row>
          {/* PRINCIPAL BODY */}

          <Col className="order-xl-1" xl="8">
            <Card className="bg-secondary shadow">
              <CardHeader className="bg-white border-0">
                <Row className="align-items-center">
                  <Col xs="8">
                    <h3 className="mb-0">Company Profile</h3>
                  </Col>
                  <Col className="text-right" xs="4">
                    {edit === true ? (
                      <Button color="primary" onClick={handleSave} size="sm">
                        Save
                      </Button>
                    ) : (
                      <Button color="primary" onClick={handleEdit} size="sm">
                        Edit
                      </Button>
                    )}
                  </Col>
                </Row>
              </CardHeader>
              <CardBody>
                <Form>
                  <h6 className="heading-small text-muted mb-4">
                    Company Information {!!info.id ? `(${info.id})` : '(unsaved)'}
                  </h6>
                  <div className="pl-lg-4">
                    <Row>
                      <Col lg="6">
                        <FormGroup>
                          <label
                            className="form-control-label"
                            htmlFor="input-about-us"
                          >
                            About Us
                          </label>
                          <Input
                            className="form-control-alternative"
                            id="about_us"
                            required
                            value={info.about_us}
                            onChange={handleInputChange}
                            type="text"
                            name="about_us"
                            placeholder="About Us"
                            disabled={!edit}
                          />
                        </FormGroup>
                      </Col>
                    </Row>

                    <Row>
                      <Col lg="6">
                        <FormGroup>
                          <label
                            className="form-control-label"
                            htmlFor="input-fast-facts"
                          >
                            Fast Facts
                          </label>
                          <Input
                            className="form-control-alternative"
                            id="fast_facts"
                            required
                            value={info.fast_facts}
                            onChange={handleInputChange}
                            type="text"
                            name="fast_facts"
                            placeholder="Fast Facts"
                            disabled={!edit}
                          />
                        </FormGroup>
                      </Col>
                      <Col lg="6">
                        <FormGroup>
                          <label
                            className="form-control-label"
                            htmlFor="input-team-members"
                          >
                            Team Members
                          </label>
                          <Input
                            className="form-control-alternative"
                            id="team_members"
                            required
                            value={info.team_members}
                            onChange={handleInputChange}
                            type="text"
                            name="team_members"
                            placeholder="Team Members"
                            disabled={!edit}
                          />
                        </FormGroup>
                      </Col>
                    </Row>
                  </div>
                </Form>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </Container>
    </>
  );
};


