import React, { Component } from 'react';
import AskNed from './../../../../assets/images/profile_images/AskNed.jpeg'
import BottleUp from './../../../../assets/images/profile_images/BottleUp.jpeg'

const data = [
    {
        image: AskNed,
        message: 'AskNed has accepted your request',
        detailPage: '/',
    },
    {
        image: BottleUp,
        message: 'You have matched with BottleUp',
        detailPage: '/',
    },
]

class Notifications extends Component {
  constructor() {
    super();

    this.state = {
      showNotifications: false,
    }

    this.showMenu = this.showMenu.bind(this);
  }

  showMenu(event) {
    event.preventDefault();

    this.setState({
      showMenu: true,
    })
  }

  render() {
    return (
      <div>
        <button>
          Notifications
        </button>

        {
          this.state.showMenu
          ? (
            <div className="notifications">
              <div className="header">
                <div className="header-title"></div>
                <div className="header-option"></div>
              </div>
              <div className="items">
                <ul className="item-list">
                  <li>
                    <button className="card">
                      <div className="content">
                        <div className="image"></div>
                        <div className="message">
                          <div className="text"></div>
                          <div className="subtext"></div>
                          <div className="time"></div>
                        </div>
                      </div>
                      <div className="card-options"></div>
                    </button>
                  </li>
                </ul>
              </div>
              <div className="footer">
                <div className="see-all"></div>
              </div>
            </div>
          )
          : (
            null
          )
        }
      </div>

    );
  }
}

export default Notifications;
